
public class Calculations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte A = 7, B = 3;
		
		System.out.println("A\t B\t A=B\t A-B\t A*B\t A/B\t A%B\t A/B");
		System.out.println(A + "\t" + B + "\t" + (A+B) + "\t" + (A-B) + "\t" + (A*B) + "\t" + (A/B) + "\t" + (A%B) + "\t" + ((float)A/B));
	}

}
